import React from "react";
import "./About.css";
let styles = {

  display:"none",
  marginTop: "10px",
  height: "250px"
};

function Contact() {
  return (

    <form method="post" action="https://script.google.com/macros/s/AKfycbznE7mqFUfLuXiTyqTeQ_xDZnZ2bMs3h0ciYcad6g/exec"  className="container-fluid p gform">

        <div>
            <div className="form-row">
                <div className="form-group col-md-12">Thoughts? Let's Connect!</div>
            </div>
            <div className="form-row">
                <div className="form-group col-md-4 col-sm-12 col-lg-4 col-12">

                  <input type="text" className="form-field " id="inputEmail4" name="first_name" placeholder="First Name" />
                </div>
                <div className="form-group col-md-4 col-12 col-lg-4">

                  <input type="text" className="form-field" id="inputPassword4" name="last_name" placeholder="Last Name" />
                </div>
              </div>
            </div>

        <div className="form-row">
            <div class="form-group col-md-4">

              <input type="email" className="form-field" id="inputEmail4" name="email" placeholder="Email Address"/>
            </div>
            <div className="form-group col-md-4">
              <input type="text" className="form-field" name="company" id="inputPassword4" placeholder="Company(Optional)"/>
            </div>
          </div>

          <div className="form-row">
          <div className="form-group col-md-8">
            <textarea className="form-comment" name="message" id="exampleFormControlTextarea1" rows="3" placeholder="Comment" style={styles}></textarea>
          </div>
          </div>
        <input type="submit" className="btn" Submit />

        <div style={styles} className="thankyou_message">
          <p>Thanks for contacting!</p>
         </div>

    </form>
)
(function () {
  // get all data in form and return object
  function getFormData(form) {
    var elements = form.elements;
    var honeypot;

    var fields = Object.keys(elements).filter(function (k) {
      if (elements[k].name === "honeypot") {
        honeypot = elements[k].value;
        return false;
      }
      return true;
    }).map(function (k) {
      if (elements[k].name !== undefined) {
        return elements[k].name;
        // special case for Edge's html collection
      } else if (elements[k].length > 0) {
        return elements[k].item(0).name;
      }
    }).filter(function (item, pos, self) {
      return self.indexOf(item) == pos && item;
    });

    var formData = {};
    fields.forEach(function (name) {
      var element = elements[name];

      // singular form elements just have one value
      formData[name] = element.value;

      // when our element has multiple items, get their values
      if (element.length) {
        var data = [];
        for (var i = 0; i < element.length; i++) {
          var item = element.item(i);
          if (item.checked || item.selected) {
            data.push(item.value);
          }
        }
        formData[name] = data.join(', ');
      }
    });

    // add form-specific values into the data
    formData.formDataNameOrder = JSON.stringify(fields);
    formData.formGoogleSheetName = form.dataset.sheet || "responses"; // default sheet name
    formData.formGoogleSendEmail
      = form.dataset.email || ""; // no email by default

    return { data: formData, honeypot: honeypot };
  }

  function handleFormSubmit(event) {  // handles form submit without any jquery
    event.preventDefault();           // we are submitting via xhr below
    var form = event.target;
    var formData = getFormData(form);
    var data = formData.data;

    // If a honeypot field is filled, assume it was done so by a spam bot.
    if (formData.honeypot) {
      return false;
    }

    disableAllButtons(form);
    var url = form.action;
    var xhr = new XMLHttpRequest();
    xhr.open('POST', url);
    // xhr.withCredentials = true;
    xhr.setRequestHeader("Content-Type", "application/x-www-form-urlencoded");
    xhr.onreadystatechange = function () {
      if (xhr.readyState === 4 && xhr.status === 200) {
        form.reset();
        var formElements = form.querySelector(".form-elements")
        if (formElements) {
          formElements.style.display = "none"; // hide form
        }
        var thankYouMessage = form.querySelector(".thankyou_message");
        if (thankYouMessage) {
          thankYouMessage.style.display = "block";
        }
      }

      setTimeout(function () {
        document.getElementsByClassName("thankyou_message").innerHTML = '';
      }, 3000);
    };
    // url encode form data for sending as post data
    var encoded = Object.keys(data).map(function (k) {
      return encodeURIComponent(k) + "=" + encodeURIComponent(data[k]);
    }).join('&');
    xhr.send(encoded);
  }

  function loaded() {
    // bind to the submit event of our form
    var forms = document.querySelectorAll("form.gform");
    for (var i = 0; i < forms.length; i++) {
      forms[i].addEventListener("submit", handleFormSubmit, false);
    }
  };
  document.addEventListener("DOMContentLoaded", loaded, false);

  function disableAllButtons(form) {
    var buttons = form.querySelectorAll("button");
    for (var i = 0; i < buttons.length; i++) {
      buttons[i].disabled = true;
    }
  }
})
}

export default Contact;
